import { Add, Remove } from '@material-ui/icons';
import React from 'react'
import styled from 'styled-components';
import Announcement from '../components/Announcement';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import { mobile } from '../responsive';

const Container = styled.div``;

const Wrapper = styled.div`
    padding:20px;
    ${mobile({
        fontSize:"13px" ,
        margin:"20px 10px" 
          })}

`

const Title = styled.h1`
    font-weight:300;
    text-align:center;
`

const Top = styled.div`
    display:flex;
    justify-content:space-between;
    align-items:center;
    padding:20px

`
const TopButton = styled.button`
    padding:10px;
    cursor:pointer;
    font-weight:600;
    border: ${(props) => props.type === "filled" && "none"};
    background-color: ${(props) =>props.type === "filled" ? "black" : "#F3A847"};
    color: ${(props) =>props.type === "filled" && "white"};


`
const TopTexts = styled.div`

`;



const TopText = styled.span`
text-decoration:underline;
margin:0 10px;
cursor:pointer;
`;

    


const Bottom = styled.div`
    display:flex;
    justify-content:space-between;

`


const Info = styled.div`
    flex:3;
`



const Product = styled.div`
    display:flex;
    justify-content:space-between;
    /* margin:10px 0; */

`

const ProductDetail = styled.div`
    flex:2;
    display:flex;


`

const Image = styled.img`
width:200px;

`

const Details = styled.div`
    padding:20px;
    display:flex;
    flex-direction:column;
    justify-content:space-around;


`

const ProductName = styled.span``

const ProductID = styled.span``

const ProductColor = styled.span`
width:20px;
height:20px;
border-radius:50%;
background-color : ${(props=>props.color)}
`

const ProductSize = styled.span`
`

const PriceDetail = styled.div`
    display:flex;
    justify-content:center;
    align-items:center;
    flex:1;
    flex-direction:column;

`
const ProductAmountContainer = styled.div`
    display:flex;
    align-items:center;
    margin-bottom:20px;

`
    
const ProductAmount = styled.div`
    margin:5px;
    font-size:24px;


`;

const ProductPrice = styled.div`

    font-size:18px;
    font-weight:400;

`;
const Hr = styled.div`
    background-color:#949393;
    border:none;
    height:1px;
    margin-bottom:19px;
    margin-right:15px;

`


const Summary = styled.div`
  flex: 1;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: 55vh;


`
const SummaryTitle = styled.h1`
    font-weight:200;
    font-size:24px;

`;


const SummaryItem = styled.div`
    margin:30px 0;
    display:flex;
    justify-content:space-between;
    font-weight: ${(props) => props.type === "total" && "500"};
    font-size: ${(props) => props.type === "total" && "20px"};

`;


const SummaryItemText = styled.span``;


const SummaryItemPrice = styled.span``;

const SummaryButton = styled.button`
    width:100%;
    padding:10px;
    background-color:black;
    color:white;
    font-weight:600;


`;





function Cart() {
  return (
    <Container>
        <Navbar/>
        <Announcement/>
        <Wrapper>
            <Title>Your Bag</Title>
            <Top>
                <TopButton>CONTINUE SHOPPING</TopButton>
                <TopTexts>
                    <TopText>Shopping Bag(2)</TopText>
                    <TopText>Your Wishlist (0)</TopText>
                </TopTexts>
                <TopButton type="filled">CHECKOUT NOW</TopButton>
            </Top>
            <Bottom>
                <Info>
                    <Product>
                        <ProductDetail>
                            <Image src = "https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1614188818-TD1MTHU_SHOE_ANGLE_GLOBAL_MENS_TREE_DASHERS_THUNDER_b01b1013-cd8d-48e7-bed9-52db26515dc4.png?crop=1xw:1.00xh;center,top&resize=480%3A%2A"/>
                            <Details>
                                <ProductName><b>Product: </b>JESSIE THUNDER SHOES</ProductName>
                                <ProductID><b>ID: </b>196810459</ProductID>
                                <ProductColor color="black"/>
                                <ProductSize><b>Size: </b>37.5</ProductSize>
                            </Details>
                        </ProductDetail>
                        <PriceDetail>
                            <ProductAmountContainer>
                                <Remove/>
                                <ProductAmount>2</ProductAmount>
                                <Add/>
                            </ProductAmountContainer>
                            <ProductPrice>$30</ProductPrice>
                        </PriceDetail>
                    </Product>
                    <Hr/>
                    <Product>
                        <ProductDetail>
                            <Image src = "https://www.digitaltrends.com/wp-content/uploads/2020/02/corsair-vengeance-rgb-pro-led-16gb-3200mhz.jpg?fit=720%2C720&p=1"/>
                            <Details>
                                <ProductName><b>Product: </b>Corsair vengeance pro 16gb ram</ProductName>
                                <ProductID><b>ID: </b>1968956789</ProductID>
                                <ProductColor color=""/>
                                {
                                    /* <ProductSize><b>Size: </b> </ProductSize> */
                                }
                            </Details>
                        </ProductDetail>
                        <PriceDetail>
                            <ProductAmountContainer>
                                <Remove/>
                                <ProductAmount>2</ProductAmount>
                                <Add/>
                            </ProductAmountContainer>
                            <ProductPrice>$182.69</ProductPrice>
                        </PriceDetail>
                    </Product>
                </Info>
                <Summary>
                    <SummaryTitle>ORDER SUMMARY</SummaryTitle>
                            <SummaryItem>
                                <SummaryItemText>Subtotal</SummaryItemText>
                                <SummaryItemPrice>$365.38 </SummaryItemPrice>
                            </SummaryItem>
                            <SummaryItem>
                                <SummaryItemText>Estimated shipping</SummaryItemText>
