import { ArrowLeftOutlined, ArrowRightOutlined } from '@material-ui/icons'
import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    height:100vh;
    width :100%;
    display:flex;
    position:relative;

`
const Wrapper = styled.div`
    height:100%;
`

const Arrow = styled.div`
    width:50px;
    height:50px;
    background-color:#fff7f7;
    border-radius:50%;
    display:flex;
    justify-content:center;
    align-items:center;
    position:absolute;
    top:0;
    bottom:0;
    left: ${props => props.direction === "left" && "10px"};
    right: ${props => props.direction === "right" && "10px"};
    margin:auto;
    cursor:pointer;
    opacity:0.5;    

`

const Slide = styled.div`
    display:flex;
    height:100vh;
    align-items:center;
    width:100vw;
`
const ImgContainer = styled.div`
    flex:1;
    height:100%;

`
const Image = styled.img`
    height:80%
`


const InfoContainer = styled.div`
    flex:1;
    padding:50px;


`

const Title = styled.h1``
const Description = styled.h1``
const Button = styled.h1``




function Slider() {
  return (
    <Container>
        <Arrow direction="left">
            <ArrowLeftOutlined/>
        </Arrow>
        <Wrapper>
            <Slide>
                <ImgContainer>
                <Image src = "https://cdn.cliqueinc.com/posts/295270/pleated-skirt-outfits-295270-1631901459734-promo.700x0c.jpg" />
                </ImgContainer>
                <InfoContainer>
                    <Title>Summer Sale</Title>
                    <Description>DON'T COMPROMISE </Description>
                    <Button></Button>
                </InfoContainer>
            </Slide>
        </Wrapper> 
        <Arrow direction = "right">
            <ArrowRightOutlined/>
        </Arrow>
    </Container>
  )
}

export default Slider
